import React, { Component, useState, useEffect } from "react";
import { Map, TileLayer, withLeaflet, MapControl } from "react-leaflet";
import MapInfo from "./MapInfo";
import Routing from "./RoutingMachine";



import L from "leaflet";
import "leaflet-routing-machine";
// import "lrm-google";
// import { withLeaflet } from "react-leaflet";
import { MapLayer } from "react-leaflet";


const accessToken = 'pk.eyJ1IjoibWFwYm94IiwiYSI6ImNpejY4M29iazA2Z2gycXA4N2pmbDZmangifQ.-g_vE53SD2WrJ6tFX7QHmA';


function createLeafletElement(map) {
  // const { map } = this.props;
  let leafletElement = L.Routing.control({
    router: L.Routing.mapbox(accessToken, {
      unit: 'metric',
      profile : 'mapbox/cycling',
      language: 'en',
      alternatives: true,
      geometries: 'geojson',
      controls: { instructions: false },
      flyTo: false
    }),
    waypoints: [
      L.latLng(16.506, 80.648),
      L.latLng(17.384, 80.4866),
      L.latLng(12.971, 77.5945)
    ],
    // router: new L.Routing.Google(),
    lineOptions: {
      styles: [
        {
          color: "gray",
          opacity: 0.7,
          weight: 6
        }
      ]
    },
    addWaypoints: false,
    draggableWaypoints: true,
    fitSelectedRoutes: false,
    showAlternatives: true
  }).addTo(map.leafletElement);
  leafletElement.hide();
}






class MapComponent extends React.Component {
  constructor() {
    super();
    this.state = {
      lat: 17.4,
      lng: 78.4,
      zoom: 7,
      isMapInit: false
    };
  };



  saveMap = (map) => {
    this.map = map;
    this.setState({
      isMapInit: true
    });


  createLeafletElement()
    // Routing.apply(Routing.);
    // var route = new Routing();
    // route.createLeafletElement();
  };

  render() {
    const { lat, lng, zoom } = this.state;
    const position = [lat, lng];

    return (
      <Map center={position} zoom={zoom} ref={this.saveMap}>
        <TileLayer
          // url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          url="https://api.mapbox.com/styles/v1/mapbox/streets-v11/tiles/{z}/{x}/{y}?access_token=pk.eyJ1Ijoic2xhdmtvcHJ5dHVsYSIsImEiOiJja281cnRmdjQwODl5MnZwZGdmeW1hb3N3In0.mhPtHgPEkyIHhGCQcNJNlA"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        {this.state.isMapInit}
      </Map>
    );
  }
}

export default MapComponent;
